  `trial_limit` int(11) NOT NULL DEFAULT '0',
  `expiration_number` int(10) UNSIGNED NOT NULL,
  `expiration_period` enum('Day','Week','Month','Year') NOT NULL,
  PRIMARY KEY (`code_id`,`level_id`),
  KEY `initial_payment` (`initial_payment`)
